mapper.select:
select video_id, video_name, video_url, cm.* from course_video join course_menu cm on course_video.menu_id = cm.menu_id
and video_id = #{videoId,jdbcType=INTEGER}
and video_name like concat('%',#{videoName,jdbcType=VARCHAR},'%')
and course_id = #{courseMenu.menuId,jdbcType=INTEGER}

mapper.update:
update course_video


    video_name = #{videoName,jdbcType=VARCHAR},


    video_url = #{videoUrl,jdbcType=VARCHAR},


    menu_id = #{courseMenu.menuId,jdbcType=INTEGER}


where video_id =#{videoId,jdbcType=INTEGER}
